import React, { Fragment, useEffect, createRef } from "react";
import ReactDOM from "react-dom";
import QRCode from "./Component/qrCode";
import './App.css';

function App() {
  const qrCodeRef = createRef();

  useEffect(() => {
    new QRCode(qrCodeRef.current, {
      text: "hello",
      width: 166,
      height: 256
    });
  }, []);
  return (
    <Fragment>
      <div ref={qrCodeRef} />
    </Fragment>
  );
}

export default App;
